	end
	
	-- generate and display the checkpoint status string
	local checkpoint_string = string.format("Next: %i: %s\n",checkpoints_list[curr_checkpoint][1],checkpoints_list[curr_checkpoint][2])

	-- if we're within 10 changes of a checkpoint, display a status bar.
	-- otherwise, just display the difference
	if(distance_to_checkpoint > 10)
	then
		checkpoint_string = checkpoint_string .. string.format("To next checkpoint: %i", distance_to_checkpoint)
	else
		checkpoint_string = checkpoint_string .. status_bar[distance_to_checkpoint + 1]
	end
	gui.pixelText(15,122,checkpoint_string,checkpoint_string_color)
end

local function initialize_checkpoints()
	local checkpoints_file = assert(io.open("kdl_checkpoints.txt", "r"))
	io.input(checkpoints_file)

	local i = 1
	checkpoints_list = {}
	repeat
		local checkpoint, description = io.read("*n", "*l")
		
		checkpoints_list[i] = {}
		checkpoints_list[i][1] = checkpoint
		checkpoints_list[i][2] = description
		i = i + 1
	until(io.read(0) == nil)
	io.close(checkpoints_file)
end

local function update_checkpoint()
	-- if we've passed a checkpoint, advance to the next one
	if(rng_changes > checkpoints_list[curr_checkpoint][1])
	then
		repeat
			curr_checkpoint = curr_checkpoint + 1
		until(rng_changes < checkpoints_list[curr_checkpoint][1])
	elseif(curr_checkpoint > 1)
	then
		if(rng_changes < checkpoints_list[curr_checkpoint - 1][1])
		then
			repeat
				curr_checkpoint = curr_checkpoint - 1
			until(rng_changes > checkpoints_list[curr_checkpoint - 1][1])
		end
	end
	
end


initialize_checkpoints()

while true do
	--if the framecount is zero, then we've reset. We need to reset our script as well
	if(emu.framecount() == 0)
	then
		reset()
	end
	
	--read the current RNG seed
	local curr_seed = memory.read_u16_be(0xD02F)
	--if the current seed is different from the last seed, then we need to update our change count
	if(curr_seed ~= last_seed)
	then
		--if the current framecount is greater than the last framecount
		--then we're going forward, so we need to search forward
		if(emu.framecount() > last_framecount)
		then
			--keep incrementing the rng_changes counter until
			--we're at the right spot
			repeat
				rng_changes = rng_changes + 1
				assert(rng_seed_progression[rng_changes]~=nil, "Something went wrong. Reached end of RNG progression.")
				--print(rng_changes, rng_seed_progression[rng_changes], curr_seed)
			until(curr_seed == rng_seed_progression[rng_changes])
		else
			--do the same as above, except decrement because we're going backwards
			repeat
				rng_changes = rng_changes - 1
				assert(rng_seed_progression[rng_changes]~=nil, "Something went wrong. Reached beginning of RNG progression.")
				--print(rng_changes, rng_seed_progression[rng_changes], curr_seed)
			until(curr_seed == rng_seed_progression[rng_changes])
		end
		update_checkpoint()
		last_seed = curr_seed
	end
	--display the value on screen
	last_framecount = emu.framecount()
	update_display()
	--the script will freeze if we don't call emu.frameadvance()
	emu.frameadvance()
end
